mapper.select:
SELECT DEF.S_CODE, DEF.S_NAME, DEF.A, IFNULL(SEA.RATE, 0) RATE FROM ( SELECT S_CODE, S_NAME, M.A FROM T_ST_CODE, ( SELECT '01' a union all SELECT '02' a union all SELECT '03' a union all SELECT '04' a union all SELECT '05' a union all SELECT '06' a union all SELECT '07' a union all SELECT '08' a union all SELECT '09' a union all SELECT '10' a union all SELECT '11' a union all SELECT '12' a ) M
WHERE S_CODE = #{s_code}
) DEF LEFT JOIN (
SELECT S_CODE, CASE WHEN S_CODE = '13211' THEN '일산(13211)' WHEN S_CODE = '13210' THEN '송도(13210)' WHEN S_CODE = '13206' THEN '구로(13206)' END S_NAME, SUBSTR(ST_DAY, 1, 7) AS YM, SUBSTR(ST_DAY, 1, 4) YY, SUBSTR(ST_DAY, 6, 2) MM, ROUND(SUM(RATE)/31, 2) AS RATE FROM ( SELECT S_CODE, ST_DAY, SUM(RATE)/24 AS RATE FROM ( SELECT S_CODE, ST_DAY, H_TIME, ROUND( ( IFNULL(ROUND(r1/s1 * 100, 2), 0) + IFNULL(ROUND(r2/s2 * 100, 2), 0) + IFNULL(ROUND(r3/s3 * 100, 2), 0) + IFNULL(ROUND(r4/s4 * 100, 2), 0) + IFNULL(ROUND(r5/s5 * 100, 2), 0) + IFNULL(ROUND(r6/s6 * 100, 2), 0) )/6, 2 ) AS RATE FROM ( SELECT S_CODE, DATE_FORMAT(st_time, '%Y-%m-%d') AS ST_DAY, DATE_FORMAT(st_time, '%H') h_time, DATE_FORMAT(st_time, '%Y-%m-%d %H') AS ST_TIME, SUM(IF ( (DATE_FORMAT(st_time, '%i') BETWEEN '00' AND '09'), (real_file_cnt), 0)) r1, SUM(IF ( (DATE_FORMAT(st_time, '%i') BETWEEN '10' AND '19'), (real_file_cnt), 0)) r2, SUM(IF ( (DATE_FORMAT(st_time, '%i') BETWEEN '20' AND '29'), (real_file_cnt), 0)) r3, SUM(IF ( (DATE_FORMAT(st_time, '%i') BETWEEN '30' AND '39'), (real_file_cnt), 0)) r4, SUM(IF ( (DATE_FORMAT(st_time, '%i') BETWEEN '40' AND '49'), (real_file_cnt), 0)) r5, SUM(IF ( (DATE_FORMAT(st_time, '%i') BETWEEN '50' AND '59'), (real_file_cnt), 0)) r6, SUM(IF ( (DATE_FORMAT(st_time, '%i') BETWEEN '00' AND '09'), (srv_file_cnt), 0)) s1, SUM(IF ( (DATE_FORMAT(st_time, '%i') BETWEEN '10' AND '19'), (srv_file_cnt), 0)) s2, SUM(IF ( (DATE_FORMAT(st_time, '%i') BETWEEN '20' AND '29'), (srv_file_cnt), 0)) s3, SUM(IF ( (DATE_FORMAT(st_time, '%i') BETWEEN '30' AND '39'), (srv_file_cnt), 0)) s4, SUM(IF ( (DATE_FORMAT(st_time, '%i') BETWEEN '40' AND '49'), (srv_file_cnt), 0)) s5, SUM(IF ( (DATE_FORMAT(st_time, '%i') BETWEEN '50' AND '59'), (srv_file_cnt), 0)) s6 FROM T_RCV_FILE WHERE DATE_FORMAT(st_time, '%Y') = #{s_date}
AND S_CODE = #{s_code}
GROUP BY S_CODE, DATE_FORMAT(st_time, '%Y-%m-%d %H') ) TMP1 ORDER BY ST_DAY, H_TIME ) TMP2 GROUP BY S_CODE, ST_DAY ) TMP3 GROUP BY S_CODE, SUBSTR(ST_DAY, 1, 7)
) SEA ON DEF.S_CODE = SEA.S_CODE AND DEF.A = SEA.MM ORDER BY S_CODE, A

SELECT NO, S_CODE, ST_TIME, ET_TIME, ( CASE P_TYPE WHEN 0 THEN 'PPI' WHEN 1 THEN 'RHI' WHEN 2 THEN 'LOS' WHEN 3 THEN 'DBS' END ) AS P_TYPE, P_PAM1, P_PAM2, P_PAM3, P_PAM4, AVT_TM, REG_dt FROM T_RCV_PARAM_INFO WHERE 1 = 1
AND S_CODE = #{s_code}
AND DATE_FORMAT(st_time, '%Y-%m-%d') = #{s_date}
ORDER BY NO DESC LIMIT ${listNum}, 10

			SELECT
					COUNT(NO) AS CNT
			  FROM  T_RCV_PARAM_INFO
			 WHERE  1 = 1

	           AND  S_CODE = #{s_code}


              AND DATE_FORMAT(st_time, '%Y-%m-%d') = #{s_date}
